export class Accordion{constructor(e,t){document.addEventListener("DOMContentLoaded",()=>{[].map.call(document.querySelectorAll(e),e=>{e.addEventListener("click",e=>{var n=e.currentTarget.closest("dt");n.classList.toggle(t),n.classList.contains(t)?n.nextElementSibling.setAttribute("aria-expanded","true"):n.nextElementSibling.setAttribute("aria-expanded","false")})})})}}